NameList = ["Erythronium_caucasicum", "Erythronium_dens_canis", "Erythronium_sibiricum", "Erythronium_japonicum", "Erythronium_taylorii", "Erythronium_tuolumnense", "Erythronium_pluriflorum", "Erythronium_purpurascens",
"Erythronium_klamathense", "Erythronium_citrinum", "Erythronium_hendersonii", "Erythronium_multiscapideum", "Erythronium_californicum", "Erythronium_helenae", "Erythronium_grandiflorum",
"Erythronium_grandiflorum_subsp_candidum", "Erythronium_montanum", "Erythronium_oregonum", "Erythronium_revolutum", "Erythronium_quinaultense", "Erythronium_elegans", "Erythronium_albidum",
"Erythronium_mesochoreum", "Erythronium_umbilicatum", "Erythronium_americanum", "Erythronium_americanum_subsp_harperi", "Erythronium_propullans"]

listoflengths = []
for item in megalist:
	print(len(item))
	listoflengths.append(len(item))

maximum = max(listoflengths)

newlist = []

for taxon in megalist:
	if len(taxon) < maximum:
		diff = maximum - len(taxon)
		addonstring = "-" * diff
		taxon = taxon + addonstring
		newlist.append(taxon)
	else:
		newlist.append(taxon)

print(newlist)

file = open("INPUTFORNEX.txt", "a")
for i in range(len(megalist)):
	print(NameList[i], newlist[i])
	end = newlist[i] + "\n"
	file.write(NameList[i])
	file.write(end)

newfile = open("ExperimentFML.txt", "a")
newtaxonlist = []
for taxon in newlist:
	newtaxon = "" #this replaces each taxon with a ~new taxon
	for char in taxon:
		if char == ".":
			newtaxon = newtaxon + "A"
		elif char == "(":
			newtaxon = newtaxon + "T"
		elif char == ")":
			newtaxon = newtaxon + "C"
		elif char == "-":
			newtaxon= newtaxon + "G"
	print(newtaxon)
	newtaxonlist.append(newtaxon)

for i in range(len(NameList)):
	end = newtaxonlist[i] + "\n"
	newfile.write(NameList[i])
	newfile.write(end)





